	Renderer::Renderer() {
		quadMat = glm::mat4(1.0f);
	}

	Renderer::~Renderer() {

		if (quadVBO != 0) {
			glDeleteBuffers(1, &quadVBO);
		}

		if (quadVAO != 0) {
			glDeleteVertexArrays(1, &quadVAO);
		}

		if (quadEBO != 0) {
			glDeleteBuffers(1, &quadEBO);
		}

		if (quadShader != nullptr) {
			delete quadShader;
		}

		if (initialized) {

			if (targetSurface != nullptr) {
				unbindSurface();
			}

			ZIXEL_INFO("Destroyed renderer.");

		}

	}

