	s32 Renderer::getStringWidth(Font* font, char text) {

		if (text == '\t') {

			size_t ind = (size_t)' ';
			if (ind >= font->glyphs.size()) return 0;

			FontGlyph* glyph = font->glyphs[ind];
			return (glyph->advanceX * 4);

		}
		
		if (text == ' ') {

			size_t ind = (size_t)' ';
			if (ind >= font->glyphs.size()) return 0;

			FontGlyph* glyph = font->glyphs[ind];
			return glyph->advanceX;

		}

		size_t ind = (size_t)text;
		if (ind >= font->glyphs.size()) {

			ind = (size_t)'?';
			if (ind >= font->glyphs.size()) return 0;

		}

		FontGlyph* glyph = font->glyphs[ind];
		return glyph->advanceX;

		return 0;

	}

